examples.javascript:
// We specify the domain of the application to authenticate to
const domain = "example.com"

// On the client side, we can generate a payload for the connected wallet to login
const loginPayload = await sdk.auth.login(domain);

// Then on the server side, we can securely verify the connected client-side address
const address = sdk.auth.verify(domain, loginPayload);

// And we can also generate an authentication token to send to the client
const token = sdk.auth.generate(domain, loginPayload);

// Finally, the token can be send from the client to the server to make authenticated requests
// And the server can use the following function to authenticate a token and verify the associated address
const address = sdk.auth.authenticate(domain, token);

examples.go:
// First we specify the domain of the application to authenticate to
domain := "example.com"

// We can then generate a payload for the connected wallet to login
// This can also be done on the client side with the web3sdks TypeScript SDK
payload, err := sdk.Auth.Login(domain, nil)

// Then, on the server, we can securely verify the connected address that signed the payload
address, err := sdk.Auth.Verify(domain, payload, nil)

// And we can also generate an authentication token to send back to the original payload sender
token, err := sdk.Auth.GenerateAuthToken(domain, payload, nil)

// Finally, the token can be use dby the original payload sender to authenticate to the backend
// And the server can use the following function to authenticate the token and verify the address
address, err := sdk.Auth.Authenticate(domain, token)

